cell.eg:
element editor
{
   att.global.attributes,
   att.datable.attributes,
   attribute role
   {
      list { ( "translator" | "guest" | "chief" | "managing" | "technical" )+ }
   }?,
   macro.phraseSeq
}

element egXML
{
   att.global.attributes,
   attribute valid { "true" | "feasible" | "false" }?,
   ( text | anyElement_egXML_1 )*
}

